DROP TABLE IF EXISTS product_ext;
CREATE TABLE `product_ext` (  `EId` mediumint(8) NOT NULL AUTO_INCREMENT,  `ProId` mediumint(8) DEFAULT '0',  `text` text,  `Volume` varchar(100) DEFAULT NULL,  `Applicable` varchar(100) DEFAULT NULL,  `SalesArea` varchar(100) DEFAULT NULL,  `Manual` varchar(100) DEFAULT NULL,  `Materials` varchar(100) DEFAULT NULL,  `Feature` varchar(255) DEFAULT NULL,  PRIMARY KEY (`EId`)) ENGINE=MyISAM AUTO_INCREMENT=5073 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS product_inquire;
CREATE TABLE `product_inquire` (  `IId` mediumint(8) NOT NULL AUTO_INCREMENT,  `ProId` varchar(100) DEFAULT NULL,  `MemberId` int(11) NOT NULL DEFAULT '0',  `SupplierId` int(11) NOT NULL DEFAULT '0',  `FirstName` varchar(25) DEFAULT NULL,  `LastName` varchar(25) DEFAULT NULL,  `Email` varchar(100) DEFAULT NULL,  `Address` varchar(200) DEFAULT NULL,  `Area` varchar(50) DEFAULT NULL,  `City` varchar(50) DEFAULT NULL,  `State` varchar(50) DEFAULT NULL,  `Country` varchar(50) DEFAULT NULL,  `PostalCode` varchar(10) DEFAULT NULL,  `Phone` varchar(20) DEFAULT NULL,  `Fax` varchar(20) DEFAULT NULL,  `Subject` varchar(100) DEFAULT NULL,  `Message` text,  `Ip` varchar(15) DEFAULT NULL,  `PostTime` int(10) DEFAULT '0',  `IsRead` tinyint(1) DEFAULT '0',  `Qty` smallint(6) NOT NULL DEFAULT '0',  `TrackingNumber` varchar(255) DEFAULT NULL,  PRIMARY KEY (`IId`)) ENGINE=MyISAM AUTO_INCREMENT=24 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS product_review;
CREATE TABLE `product_review` (  `RId` mediumint(8) NOT NULL AUTO_INCREMENT,  `ProId` mediumint(8) DEFAULT '0',  `FullName` varchar(50) DEFAULT NULL,  `Email` varchar(100) DEFAULT NULL,  `Rating` tinyint(1) DEFAULT '5',  `Contents` text,  `Ip` varchar(15) DEFAULT NULL,  `Reply` text,  `ReplyTime` int(10) DEFAULT '0',  `Display` tinyint(1) DEFAULT '0',  `PostTime` int(10) DEFAULT '0',  PRIMARY KEY (`RId`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS product_size;
CREATE TABLE `product_size` (  `SId` int(10) NOT NULL AUTO_INCREMENT,  `Size` varchar(50) DEFAULT NULL,  `MyOrder` tinyint(2) DEFAULT '0',  PRIMARY KEY (`SId`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS product_wholesale_price;
CREATE TABLE `product_wholesale_price` (  `PId` mediumint(8) NOT NULL AUTO_INCREMENT,  `ProId` mediumint(8) DEFAULT '0',  `Qty` smallint(5) DEFAULT '0',  `Price` decimal(10,2) DEFAULT '0.00',  PRIMARY KEY (`PId`)) ENGINE=MyISAM AUTO_INCREMENT=439 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS search_keyword;
CREATE TABLE `search_keyword` (  `KId` smallint(5) NOT NULL AUTO_INCREMENT,  `Keyword` varchar(50) DEFAULT NULL,  `Language` varchar(10) DEFAULT NULL,  `MyOrder` smallint(5) DEFAULT '0',  PRIMARY KEY (`KId`)) ENGINE=MyISAM AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS shipping;
CREATE TABLE `shipping` (  `SId` smallint(5) NOT NULL AUTO_INCREMENT,  `Express` varchar(100) DEFAULT NULL,  `Express_lang_1` varchar(100) DEFAULT NULL,  `FreeShippingInvocation` tinyint(1) DEFAULT '0',  `FreeShippingPrice` decimal(10,2) DEFAULT '0.00',  `Explanation` varchar(250) DEFAULT NULL,  `Explanation_lang_1` varchar(250) DEFAULT NULL,  `MyOrder` smallint(5) DEFAULT '0',  `CustomsClearance` decimal(10,2) DEFAULT NULL,  PRIMARY KEY (`SId`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS shipping_freight;
CREATE TABLE `shipping_freight` (  `FId` smallint(8) NOT NULL AUTO_INCREMENT,  `SFID` smallint(8) DEFAULT NULL,  `FreightModes` varchar(225) DEFAULT NULL,  `FreightCost` decimal(10,0) DEFAULT NULL,  `FreightTime` int(3) DEFAULT NULL,  `FreightTime_1` int(3) DEFAULT NULL,  PRIMARY KEY (`FId`)) ENGINE=MyISAM AUTO_INCREMENT=53 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS shipping_price;
CREATE TABLE `shipping_price` (  `PId` smallint(5) NOT NULL AUTO_INCREMENT,  `SId` smallint(5) DEFAULT '0',  `CId` smallint(3) DEFAULT '0',  `FirstPrice` decimal(10,2) DEFAULT '0.00',  `FirstWeight` decimal(10,2) DEFAULT '0.00',  `ExtWeight` decimal(10,2) DEFAULT '0.00',  `ExtPrice` decimal(10,2) DEFAULT '0.00',  PRIMARY KEY (`PId`)) ENGINE=MyISAM AUTO_INCREMENT=721 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS shopping_cart;
CREATE TABLE `shopping_cart` (  `CId` mediumint(8) NOT NULL AUTO_INCREMENT,  `MemberId` mediumint(8) DEFAULT '0',  `SupplierId` int(11) NOT NULL DEFAULT '0',  `SessionId` varchar(10) DEFAULT NULL,  `ProId` mediumint(8) DEFAULT '0',  `CateId` smallint(5) DEFAULT '0',  `StartFrom` smallint(5) DEFAULT '0',  `Color` varchar(50) DEFAULT NULL,  `Size` varchar(50) DEFAULT NULL,  `Name` varchar(100) DEFAULT NULL,  `Name_lang_1` varchar(100) DEFAULT NULL,  `ItemNumber` varchar(20) DEFAULT NULL,  `Weight` decimal(10,3) DEFAULT '0.000',  `PicPath` varchar(100) DEFAULT NULL,  `Price` decimal(10,2) DEFAULT '0.00',  `Qty` smallint(5) DEFAULT '0',  `Url` varchar(200) DEFAULT NULL,  `Remark` varchar(100) DEFAULT NULL,  `AddTime` int(10) DEFAULT '0',  `Property` text,  `Attr_key` text,  `Mysubmit` tinyint(1) DEFAULT NULL,  PRIMARY KEY (`CId`)) ENGINE=MyISAM AUTO_INCREMENT=318 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS site_view;
CREATE TABLE `site_view` (  `SId` int(11) NOT NULL AUTO_INCREMENT,  `Time` int(11) DEFAULT '0',  `Views` int(11) DEFAULT '0',  PRIMARY KEY (`SId`)) ENGINE=MyISAM AUTO_INCREMENT=623 DEFAULT CHARSET=utf8 COMMENT='网站流量统计';
DROP TABLE IF EXISTS supplire_manage_orders;
CREATE TABLE `supplire_manage_orders` (  `SMOId` mediumint(8) NOT NULL AUTO_INCREMENT,  `SMOrders` varchar(30) DEFAULT NULL,  `SMApply_1` tinyint(1) DEFAULT NULL,  `SMApply_2` tinyint(1) DEFAULT NULL,  `SMApply_3` tinyint(1) DEFAULT NULL,  `SMApply_submit_1` tinyint(1) DEFAULT NULL,  `SMApply_submit_2` tinyint(1) DEFAULT NULL,  `SMApply_submit_3` tinyint(1) DEFAULT NULL,  `SMPach_1` varchar(100) DEFAULT NULL,  `SMPach_2` varchar(100) DEFAULT NULL,  `SMPach_3` varchar(100) DEFAULT NULL,  `SMAffirm` tinyint(1) DEFAULT NULL,  `Invalidcause_1` varchar(255) DEFAULT NULL,  `Invalidcause_2` varchar(255) DEFAULT NULL,  `Invalidcause_3` varchar(255) DEFAULT NULL,  `Production` varchar(255) DEFAULT NULL,  `Advance_1` decimal(10,2) DEFAULT NULL,  `Advance_2` decimal(10,2) DEFAULT NULL,  `Advance_3` decimal(10,3) DEFAULT NULL,  PRIMARY KEY (`SMOId`)) ENGINE=MyISAM AUTO_INCREMENT=79 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS survey;
CREATE TABLE `survey` (  `SId` smallint(5) NOT NULL AUTO_INCREMENT,  `Subject` varchar(100) DEFAULT NULL,  `Language` tinyint(1) NOT NULL DEFAULT '0',  `MyOrder` smallint(5) DEFAULT '0',  PRIMARY KEY (`SId`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS survey_item;
CREATE TABLE `survey_item` (  `IId` tinyint(5) NOT NULL AUTO_INCREMENT,  `SId` tinyint(5) DEFAULT '0',  `ItemTitle` varchar(100) DEFAULT NULL,  `VotesCount` mediumint(8) DEFAULT '0',  PRIMARY KEY (`IId`)) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS translate;
CREATE TABLE `translate` (  `LId` smallint(5) NOT NULL AUTO_INCREMENT,  `Name` varchar(50) DEFAULT NULL,  `Url` varchar(200) DEFAULT NULL,  `LogoPath` varchar(100) DEFAULT NULL,  `Language` varchar(10) DEFAULT NULL,  `MyOrder` smallint(5) DEFAULT '0',  PRIMARY KEY (`LId`)) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS transport;
CREATE TABLE `transport` (  `TId` mediumint(11) NOT NULL AUTO_INCREMENT,  `TOId` varchar(30) DEFAULT NULL,  `Qty` int(10) DEFAULT NULL,  `TotalPrice` decimal(10,2) DEFAULT NULL,  `ShippingTime` int(10) DEFAULT NULL,  `ProductName` varchar(200) DEFAULT NULL,  `Destination` varchar(200) DEFAULT NULL,  `FNAME` varchar(100) DEFAULT NULL,  `FMoble` varchar(100) DEFAULT NULL,  `Fdeliveryaddress` varchar(255) DEFAULT NULL,  `SNAME` varchar(100) DEFAULT NULL,  `SMoble` varchar(100) DEFAULT NULL,  `Sdeliveryaddress` varchar(255) DEFAULT NULL,  `Weight` decimal(10,2) DEFAULT NULL,  `Company` varchar(255) DEFAULT NULL,  PRIMARY KEY (`TId`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS userinfo;
CREATE TABLE `userinfo` (  `UserId` smallint(5) NOT NULL AUTO_INCREMENT,  `UserName` varchar(16) DEFAULT NULL,  `Password` varchar(32) DEFAULT NULL,  `Salesman_Name` varchar(225) DEFAULT NULL,  `LastLoginTime` int(10) DEFAULT '0',  `LastLoginIp` varchar(15) DEFAULT NULL,  `Locked` tinyint(1) DEFAULT '0',  `GroupId` tinyint(1) DEFAULT NULL,  PRIMARY KEY (`UserId`)) ENGINE=MyISAM AUTO_INCREMENT=29 DEFAULT CHARSET=utf8 PACK_KEYS=0;
DROP TABLE IF EXISTS wish_lists;
CREATE TABLE `wish_lists` (  `WId` mediumint(8) NOT NULL AUTO_INCREMENT,  `MemberId` mediumint(8) DEFAULT '0',  `ProId` mediumint(8) DEFAULT '0',  `WishTime` int(10) DEFAULT '0',  PRIMARY KEY (`WId`)) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;
